sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

        // implement resource here

        var user_path = request.pathParams;
        var user = user_path.user_name;

        var query_param = request.queryParams;
        var query = query_param.ni_query;

	    var res = {'totalUserNIRecs':'0',
				   "catCounts":[],
/*				   'hr_count':'0',
				   'facilities_count':'0',
					'legal_count':'0',*/
				    'user':{}
					   };

	    var cat_count = {};

        var user_rec = new GlideRecord('sys_user');
        user_rec.get('name', user);

	    // Count of active Needit recs
	    var ga = new GlideAggregate('x_58872_needit_needit');
        ga.addQuery('u_requested_for', user_rec.sys_id);
//	    ga.addQuery('state','!=','3');
	    ga.addEncodedQuery(query);
        ga.addAggregate('COUNT', 'u_requested_for');
        ga.query(query);
        if (ga.hasNext()) {
            while (ga.next()) {
     //           gs.info(ga.getAggregate('COUNT', 'u_requested_for'));
				res.totalUserNIRecs = ga.getAggregate('COUNT', 'u_requested_for');
            }
        }

	    // Count of active NeedIt records for the user by Request type
        ga = new GlideAggregate('x_58872_needit_needit');
        ga.addAggregate('COUNT', 'u_request_type');
        ga.addQuery('u_requested_for', user_rec.sys_id);
//	    ga.addQuery('state','!=','3');
	    ga.addEncodedQuery(query);
        ga.query();
        if (ga.hasNext()) {
            while (ga.next()) {
				switch (ga.u_request_type + ''){
					case 'hr' :
			//			res.hr_count = ga.getAggregate('COUNT', 'u_request_type');
						cat_count.requestType = ga.u_request_type.getDisplayValue();
						cat_count.count = ga.getAggregate('COUNT', 'u_request_type');
						res.catCounts.push(cat_count);						
						break;

				    case 'facilities' :
			//			res.facilities_count = ga.getAggregate('COUNT', 'u_request_type');
						cat_count.requestType = ga.u_request_type.getDisplayValue();
						cat_count.count = ga.getAggregate('COUNT', 'u_request_type');
						res.catCounts.push(cat_count);	
						break;

					case 'legal' :
			//			res.legal_count = ga.getAggregate('COUNT', 'u_request_type');
						cat_count.requestType = ga.u_request_type.getDisplayValue();
						cat_count.count = ga.getAggregate('COUNT', 'u_request_type');
						res.catCounts.push(cat_count);	
						break;						
				}
  //              gs.info(ga.u_request_type + ' : ' + ga.getAggregate('COUNT', 'u_request_type'));
  //				response.count = ga.getAggregate('COUNT', 'u_request_type');
            }
        }

	    res.user.name =user;
	    res.user.sys_id =user_rec.sys_id;

//	    res = JSON.stringify(res);

	    response.setBody(res);
	    response.setStatus('200');       

})(request, response);
